// import {catImgs, names, cityCountry, catSpecies, birthday} from "../data/catData";
import React, { useState } from "react";
import { catImgs } from "../data/catData";
import { Cats } from "../data/catObject";

const List = ({ Cat }) => {
  console.log(Cat);
  return (
    <React.Fragment>
      {Cat.map((cat) => {
        const { name, age, img, euthanizeDay, species, id } = cat;
        return (
          <article key={id}>
            <p>{name}</p>
            <p>{age}</p>
            <img src={img} alt={species} width="50px" height="auto"></img>
            <p>{euthanizeDay} left before euthnaizing</p>
          </article>
        );
      })}
    </React.Fragment>
  );
};

export const App = () => {
  const fullCatArr = Cats.filter((c) => c.euthanizeDay < 7);
  const [CatIndex, setCatIndex] = useState(3);
  const [Cat, setCat] = useState(fullCatArr.filter((c, i) => i < CatIndex));
  const moreCats = () => {
    setCatIndex(CatIndex + 3);
    setCat(fullCatArr.filter((e, i) => i < CatIndex));
  };

  

  return (
    <section>
      <h3>{fullCatArr.length} cats about to being euthanized within the next week</h3>
      <List Cat={Cat}></List>
      
      <button onClick={moreCats}>{Cat.length==fullCatArr.length ? "end of page":"more cats"}</button>
    </section>
  );
};
