// import {catImgs, names, cityCountry, catSpecies, birthday} from "../data/catData";
import React, { useContext, useState } from "react";
import { Cats } from "../data/catObject";

const List = ({ Cat, notInterested }) => {
  return (
    <React.Fragment>
      {Cat.map((cat) => {
        if (cat.id) {
          const { name, age, img, euthanizeDay, species, id } = cat;
          return (
            <article key={id}>
              <p>{name}</p>
              <p>{age} years old</p>
              <img src={img} alt={species} width="50px" height="auto"></img>
              <p>{euthanizeDay} days left before euthnaizing</p>
              <button onClick={() => notInterested(Cat, id)}>
                not interested
              </button>
            </article>
          );
        } else {
          return <div>read more</div>;
        }
      })}
    </React.Fragment>
  );
};

