// import {catImgs, names, cityCountry, catSpecies, birthday} from "../data/catData";
import React, { useState } from "react";
import { Cats } from "../data/catObject";

const List = ({ Cat }) => {
  Cat.NotInterested = false;

  return (
    <React.Fragment>
      {Cat.map((cat) => {
        const { name, age, img, euthanizeDay, species, id, NotInterested } =
          cat;
        if (NotInterested) {
          return <div>More</div>
        } else {
          return (
            <article key={id}>
              <p>{name}</p>
              <p>{age} years old</p>
              <img src={img} alt={species} width="50px" height="auto"></img>
              <p>{euthanizeDay} days left before euthnaizing</p>
              <button onClick={() => Cat.NotInterested === true}>
                not interested
              </button>
            </article>
          );
        }
      })}
    </React.Fragment>
  );
};

export const Euthanasia = () => {
  const fullCatArr = Cats.filter((c) => c.euthanizeDay < 7);
  const [CatIndex, setCatIndex] = useState(3);
  const catArr = (CatIndex) => fullCatArr.filter((c, i) => i < CatIndex);
  const [Cat, setCat] = useState(catArr(CatIndex));

  const moreCats = () => {
    setCatIndex(CatIndex + 3);
    setCat(catArr(CatIndex));
  };

  const MoreCatNbutton = () => <button onClick={moreCats}>more cats</button>;

  return (
    <section>
      <h3>
        {fullCatArr.length} cats about to be euthanized within the next week
      </h3>
      <List Cat={Cat} NotInterested></List>
      {Cat.length === fullCatArr.length ? "end of page" : <MoreCatNbutton />}
    </section>
  );
};
