import fetch from "node-fetch";
import React, { useEffect, useState } from "react";

const Loading = ({description, image, life_span, name, origin}) => {
    console
  return (
    <div>
      <h3>Loading...</h3>
    </div>
  );
};


const Cats = ({ props }) => {
    console.log(props);
    return (
        <section>
      <div>
        <h3>Hello kitten</h3>
        {props.map((cat) => (
            <Cat key={cat.id} props></Cat>
            ))}
      </div>
    </section>
  );
};

const Cat = () => {
  return <div></div>;
};

export const CatApi = () => {
  const [IsLoading, setIsLoading] = useState(true);
  const [CatArr, setCatArr] = useState([]);

  const url = "https://api.thecatapi.com/v1/breeds";

  const fetchCats = async () => {
    setIsLoading(true);
    try {
      const response = await fetch(url);
      const cats = await response.json();
      setIsLoading(false);
      setCatArr(cats);
    } catch (err) {
      setIsLoading(false);
      console.log(err);
    }
  };

  useEffect(() => {
    fetchCats();
  }, []);

  if (IsLoading) {
    return (
      <div>
        <Loading></Loading>
      </div>
    );
  } else {
    return (
      <div>
        <Cats props={CatArr}></Cats>
      </div>
    );
  }
};
