// import {catImgs, names, cityCountry, catSpecies, birthday} from "../data/catData";
import React, { useContext, useState } from "react";
import { Cats } from "../data/catObject";

const List = ({ Cat, notInterested }) => {
  if(Cat.every(c=>c===undefined))
  return (
    <React.Fragment>
      {Cat.map((cat,i,arr) => {
        const { name, age, img, euthanizeDay, species, id } = cat;
        if (name) {
          return (
            <article key={id}>
              <p>{name}</p>
              <p>{age} years old</p>
              <img src={img} alt={species} width="50px" height="auto"></img>
              <p>{euthanizeDay} days left before euthnaizing</p>
              <button onClick={() => notInterested(id)}>not interested</button>
            </article>
          );
        } else {
          return <div key={id}>...read more</div>;
        }
      })}
    </React.Fragment>
  );
};

export const Euthanasia = () => {
  const [FullCatArr, setFullCatArr] = useState(
    Cats.filter((c) => c.euthanizeDay < 7)
  );
  // const FullCatArr = Cats.filter((c) => c.euthanizeDay < 7);
  const [CatIndex, setCatIndex] = useState(3);
  const catArr = (CatIndex) => FullCatArr.filter((c, i) => i < CatIndex);
  const [Cat, setCat] = useState(catArr(CatIndex));

  const moreCats = () => {
    setCatIndex(CatIndex + 3);
    setCat(catArr(CatIndex));
  };


  const notInterested = (ID) => {
    FullCatArr[FullCatArr.findIndex(
      (c)=>{
        let isC = c!==undefined? true:false;
        return isC? c.id==ID:false;
      })]["name"]=undefined;
    setFullCatArr(FullCatArr);
    setCat(catArr(CatIndex));
  };

  const MoreCatNbutton = () => <button onClick={moreCats}>more cats</button>;

  return (
    <section>
      <h3>
        {FullCatArr.length} cats about to be euthanized within the next week
      </h3>
      <List Cat={Cat} notInterested={notInterested}></List>
      {Cat.length === FullCatArr.length ? "end of page" : <MoreCatNbutton />}
    </section>
  );
};
