import React, { useEffect, useState } from "react";
// import getData from "../data/catApi";

const Origin = ({ Cat, setFilter }) => {
  const origins = [...new Set(Cat.map((c) => c.origin))];
  return (
    <div>
      {origins.map((origin) => {
        return (
          <button key={origin} onClick={() => setFilter(origin)}>
            {origin}
          </button>
        );
      })}
    </div>
  );
};

const BodyProperty = ({ Cat, setFilteredCats, setFirst }) => {
  const body = ["hairless", "natural", "rare", "suppressed_tail", "short_legs"];
  const onClick = (e) => {
    setFilteredCats(Cat.filter((c) => c[`${e.target.value}`] === 1));
    setFirst(false);
  };

  return (
    <div>
      {body.map((b) => {
        return (
          <button key={b} onClick={onClick} value={b}>
            {b}
          </button>
        );
      })}
    </div>
  );
};

const Property = ({ Cat, setFilteredCats, setFirst }) => {
  const properties = [
    "adaptability",
    "affection_level",
    "child_friendly",
    "dog_friendly",
    "energy_level",
    "grooming",
    "health_issues",
    "intelligence",
    "shedding_level",
    "social_needs",
    "stranger_friendly",
  ];

  const onClick = (e) => {
    setFilteredCats(Cat.filter((c) => c[`${e.target.value}`] > 3));
    setFirst(false);
  };

  return (
    <div>
      {properties.map((p) => {
        return (
          <button key={p} onClick={onClick} value={p}>
            {p}
          </button>
        );
      })}
    </div>
  );
};

const ReadMore = ({ Cat }) => {
  const {
    origin,
    affection_level,
    child_friendly,
    dog_friendly,
    energy_level,
    grooming,
    health_issues,
    intelligence,
    shedding_level,
    social_needs,
    stranger_friendly,
    hairless,
    natural,
    rare,
    suppressed_tail,
    short_legs,
  } = Cat;

  const [ShowMore, setShowMore] = useState(false);

  const Show = () => {
    return (
      <div>
        <p>affection_level: {affection_level} out of 5</p>
        <p>child_friendly: {child_friendly} out of 5</p>
        <p>dog_friendly: {dog_friendly} out of 5</p>
        <p>energy_level: {energy_level} out of 5</p>
        <p>grooming: {grooming} out of 5</p>
        <p>health_issues: {health_issues} out of 5</p>
        <p>intelligence: {intelligence} out of 5</p>
        <p>shedding_level: {shedding_level} out of 5</p>
        <p>social_needs: {social_needs} out of 5</p>
        <p>stranger_friendly: {stranger_friendly} out of 5</p>
        <div>
          <h5>special properties:</h5>
          <p>originated from {origin}</p>
          <p>{hairless ? "hairless" : null}</p>
          <p>{natural ? "natural species" : null}</p>
          <p>{rare ? "rare sepcies" : null}</p>
          <p>{suppressed_tail ? "suppressed_tail" : null}</p>
          <p>{short_legs ? "short_legs" : null}</p>
        </div>
      </div>
    );
  };

  return (
    <div>
      {ShowMore && <Show />}
      <button onClick={() => setShowMore(!ShowMore)}>
        {ShowMore ? "show less" : "...read more"}
      </button>
    </div>
  );
};

const Classify = ({ Cat }) => {
  const defaultImg =
    "https://static.scientificamerican.com/sciam/cache/file/32665E6F-8D90-4567-9769D59E11DB7F26_source.jpg?w=390&h=520&7E4B4CAD-CAE1-4726-93D6A160C2B068B2";
  return (
    <div>
      {Cat.map((c) => {
        const { id, name, origin, description, life_span, image, temperament } =
          c;

        return (
          <article className="cat-article" key={id}>
            <div className="cat-article-col">
              <img
                className="cat-img"
                src={image ? image.url : defaultImg}
                alt={name}
                width="150px"
                height="auto"
              ></img>
            </div>
            <div className="cat-article-col">
              <h4 className="cat-article-name">{name}</h4>
              <p>the average life spand: {life_span}</p>
              <p>origin: {origin}</p>
              <p>description: {description}</p>
              <p>temperament: {temperament}</p>
              <ReadMore Cat={c}></ReadMore>
            </div>
          </article>
        );
      })}
    </div>
  );
};

export const CatSpecies2 = () => {
  const url = "https://api.thecatapi.com/v1/breeds";
  const getCat = async () => {
    const response = await fetch(url);
    const Cat = await response.json();
    setCat(Cat);
    setLoading(false);
  };

  useEffect(() => getCat(), [url]);
  const [Cat, setCat] = useState([]);
  const [Filter, setFilter] = useState("all");
  const [FilteredCats, setFilteredCats] = useState(Cat);
  const [First, setFirst] = useState(true);

  const [Loading, setLoading] = useState(true);

  useEffect(() => {
    if (Filter === "all") {
      setFirst(true);
    } else {
      setFirst(false);
      setFilteredCats(Cat.filter((c) => c.origin === Filter));
    }
  }, [Filter]);

  if (Loading) {
    return <div>Loading...</div>;
  }
  if (Loading === false) {
    return (
      <main>
        <section>
          <div>
            <h2>Get to know cats</h2>
          </div>
          <div>
            <button onClick={() => setFirst(true)}>all</button>
            <Origin Cat={Cat} setFilter={setFilter} />
            <Property
              Cat={Cat}
              setFilteredCats={setFilteredCats}
              setFirst={setFirst}
            />
            <BodyProperty
              Cat={Cat}
              setFilteredCats={setFilteredCats}
              setFirst={setFirst}
            />
            <Classify Cat={First ? Cat : FilteredCats}></Classify>
          </div>
        </section>
      </main>
    );
  }
};
