import React, { useEffect, useState } from "react";
import { FaEdit, FaTrash } from "react-icons/fa";

const AlertCpt = ({ type, msg, removeAlert, List }) => {
  //danger for red
  //success for green
  useEffect(() => {
      const timeout = removeAlert();
      return ()=> clearTimeout(timeout)
  }, [List])

  return <p className={`alert alert${type}`}>{msg}</p>;
};

const Review = ({ reviews, removeReview }) => {
  return (
    <div>
      {reviews.map((review) => {
        const { id, title } = review;
        return (
          <article key={id}>
            <p>{title}</p>
            <div>
              <button type="button">
                <FaEdit />
              </button>
              <button type="button" onClick={() => removeReview(id)}>
                <FaTrash />
              </button>
            </div>
          </article>
        );
      })}
    </div>
  );
};

export const Reviews = () => {
  const [Name, setName] = useState("");
  const [List, setList] = useState([]);
  const [IsEditing, setIsEditing] = useState(false);
  const [EditId, setEditId] = useState(null);
  const [Alert, setAlert] = useState({ show: false, msg: "", type: "" });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!Name) {
      showAlert(true, "please enter value", "danger");
    } else if (Name && IsEditing) {
    } else {
      showAlert(true, "thank you for your review", "success");

      const newReview = { id: new Date().getTime().toString(), title: Name };
      setList([...List, newReview]);
      setName("");
    }
  };

  const showAlert = (show = false, msg = "", type = "") => {
    setAlert({ show, msg, type });
  };

  const removeAlert = () => setTimeout(() => showAlert(false, "", ""), 3000);

  const removeReview = (id) => {
    showAlert(true, "successfully removed the review", "danger");
    setList(List.filter((review) => review.id !== id));
  };

  const editReview = (id)

  return (
    <section>
      <div>
        <form onSubmit={handleSubmit}>
          {Alert.show && <AlertCpt {...Alert} removeAlert={removeAlert} List={List} />}
          <h3>reviews</h3>
          <div>
            <input
              type="text"
              placeholder="e.g. she can cook/ 착해!"
              value={Name}
              onChange={(e) => setName(e.target.value)}
            ></input>
            <button type="submit">{IsEditing ? "edit" : "submit"}</button>
          </div>
        </form>
        {List.length > 0 && (
          <div>
            <Review reviews={List} removeReview={removeReview}></Review>
            <button>clear reviews</button>
          </div>
        )}
      </div>
    </section>
  );
};
